function Person(name, surname, age) {
    this.firstName = name;
    this.lastName = surname;
    this.age = age;
}

myMother = new Person('Linda', 'Cook', 66);
myFather = new Person('Rick', 'Handley', 67);
myBrother = new Person('Ian', 'Handley', 39);

myFather.hometown = 'Newark';

myFather.fullName =  function() {
    return this.firstName + this.lastName;
}

console.log("My mom's name is " + myMother.firstName + ". She is " + myMother.age + " years old.");
console.log(`My dad's name is ${myFather.firstName}. He is ${myFather.age} years old. He was born in ${myFather.hometown}`)
console.log(`My Dad's full name is ${myFather.fullName}.`);
